export default function MenuHome({ passarTela, setFormularioARenderizar }){
    useEffect(() => {
        const menu = document.querySelectorAll('.itemEdicao')
        menu.forEach(e => {
            e.addEventListener('click', pegarId)
        });
    })

    function pegarId() {
        const idComponente = this.id

        setFormularioARenderizar(arrayComponentes.idComponente)
    }
